sql.where:
 a.id = b.mark_id 
		and ROOT_ID = #{rootId}
    	and BRANCH_ID = #{branchId}
    	and LEAF_ID = #{leafId}
    	and a.`NAME` like CONCAT('%',#{name,jdbcType=VARCHAR},'%')

mapper.select:
 SELECT count(*) FROM 
(SELECT a.`ID` FROM gateway_api a inner join gateway_api_group b on a.GROUP_ID = b.id  
 ) a 


	inner join (
		select distinct b.api_id from gateway_mark a,gateway_api_mark b 

	) b on a.id = b.api_id

SELECT * FROM (SELECT a.`ID`,a.GROUP_ID,a.`NAME`,a.`DESCRIPTION`,a.special,a.`STATUS`,a.HTTP_METHOD,a.IS_AUTH,a.IS_LOGIN,a.AUTH_VERSION,a.SERVICE_NAME, a.SERVICE_METHOD,a.SERVICE_VERSION,a.TIME_OUT,a.CREATE_BY,a.UPDATE_BY,a.`CDT`,a.`UDT`,a.`VERSION`,b.name as group_name FROM gateway_api a inner join gateway_api_group b on a.GROUP_ID = b.id ) a
inner join ( select distinct b.api_id from gateway_mark a,gateway_api_mark b
) b on a.id = b.api_id
ORDER BY a.`NAME` asc, a.`CDT` DESC LIMIT #{skip}, #{size}
